import React, { useRef, useEffect } from 'react';
import * as THREE from 'three';

let Right = 0;
let Down = 0;

const ThreeScene = () => {
  const sceneRef = useRef();

  useEffect(() => {
    // Set up your Three.js scene here
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer();
    renderer.setSize(window.innerWidth, window.innerHeight);
    sceneRef.current.appendChild(renderer.domElement);

    // Create a cube for example
    const geometry = new THREE.BoxGeometry();
    const material = new THREE.MeshBasicMaterial({ color: 0x00ff00 });
    const cube = new THREE.Mesh(geometry, material);
    scene.add(cube);

    camera.position.z = 5;

    // Animation loop
    const animate = () => {
      requestAnimationFrame(animate);

      // Rotate the cube
      cube.rotation.x += Right;
      cube.rotation.y += Down;

      renderer.render(scene, camera);
    };

    animate();

    // Clean up on component unmount
    return () => {
      // Dispose Three.js objects, clean up resources, etc.
      renderer.dispose();
    };
  }, []);

  const leftRight = () => {
    Right = 0.01; // Update the rotation speed or value as needed
  };

  return (
    <>
      <div ref={sceneRef} />
      <button onClick={leftRight}>Move right</button>
    </>
  );
};

export default ThreeScene;
