import React, { useRef, useEffect, useState } from 'react';
import * as THREE from 'three';

const ThreeScene = () => {
  const sceneRef = useRef();
  const [rotateCube, setRotateCube] = useState(false);
  const cubeRef = useRef();

  useEffect(() => {
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer();
    renderer.setSize(window.innerWidth, window.innerHeight);
    sceneRef.current.appendChild(renderer.domElement);

    const geometry = new THREE.BoxGeometry();
    const material = new THREE.MeshBasicMaterial({ color: 0x00ff00 });
    const cube = new THREE.Mesh(geometry, material);
    scene.add(cube);
    cubeRef.current = cube; // Store the cube reference

    camera.position.z = 5;

    const animate = () => {
      requestAnimationFrame(animate);

      if (rotateCube && cubeRef.current) {
        // Rotate the existing cube
        cubeRef.current.rotation.x += 0.01;
        cubeRef.current.rotation.y += 0.01;
      }

      renderer.render(scene, camera);
    };

    animate();

    return () => {
      renderer.dispose();
    };
  }, [rotateCube]);

  const leftRight = () => {
    setRotateCube(!rotateCube); // Toggle the rotation state
  };

  return (
    <>
      <div ref={sceneRef} />
      <button onClick={leftRight}>Move right</button>
    </>
  );
};

export default ThreeScene;
